import React, {useState} from "react";

// form
    // set states
    // form values
    // handle change <=> state conjuction
    // Handle submission
// conditional rendering
const ContactForm = () => {
    // const [variable, setFn] = usestate(0)
    const [formData, setFormData] = useState({
            date: "",
            description:"",
            category:"",
            amount:""
    })
    const [isSubmitted, SetIsSubmitted] = useState(false)

const handleChange = (e) =>{
    const {name,value} = e.target
    setFormData(init =>({
        ...init,
        [name]:value
    }))
}

const handleSubmit = (e) => {
    console.log(formData)
    SetIsSubmitted(true)
}

if(isSubmitted){
    return <div>TRANSACTION SUBMITTED</div>
}
return(
    <>
        <div className="container">  
            <form id="Transaction" action="" method="post" onSubmit={handleSubmit}>
                <h3>Transaction Form</h3>
                <fieldset>
                    <input 
                        placeholder="date"
                        type="date"
                        tabIndex="1"
                        required
                        autoFocus
                        name="date"
                        value={formData.date}
                        onChange={handleChange}
                         />
                </fieldset>
                <fieldset>
                    <input 
                        placeholder="description" 
                        type="text" 
                        tabIndex="2" 
                        required
                        name="text"
                        value={formData.text}
                        onChange={handleChange}

                        />
                </fieldset>
                <fieldset>
                    <input 
                        placeholder="category" 
                        type="text" 
                        tabIndex="3"
                        name="phone"
                        value={formData.category}
                        onChange={handleChange}

                        />
                </fieldset>
                <fieldset>
                    <input 
                        placeholder="Your Web Site (optional)" 
                        type="url" 
                        tabIndex="4" 
                        name="website"
                        value={formData.website}
                        onChange={handleChange}

                         />
                </fieldset>
                <fieldset>
                    <textarea 
                        placeholder="Type your message here...." 
                        tabIndex="5" 
                        name="message"
                        value={formData.message}
                        onChange={handleChange}

                        ></textarea>
                </fieldset>
                <fieldset>
                    <button name="submit" type="submit" id="contact-submit" data-submit="...Sending">ADD TRANSACTION</button>
                </fieldset>
            </form>
        </div>
    </>
)
}

export default ContactForm;
